package com.example.stresstest;import android.app.Application;import androidx.room.Room;import com.example.stresstest.db.AppDataBase;public class App extends Application {    private static App instance;    private AppDataBase appDataBase;    @Override    public void onCreate() {        super.onCreate();        instance =  this;        appDataBase = Room.databaseBuilder(this,                AppDataBase.class, "database_battery_log").build();    }    public static Application getInstance() {        return instance;    }    public AppDataBase getAppDataBase() {        return appDataBase;    }}