package com.example.stresstest.benchmark;import android.util.Log;import com.example.stresstest.benchmark.cpu.CPUBenchmark;import com.example.stresstest.benchmark.file.FilesBenchmark;import com.example.stresstest.benchmark.hash.HashingBenchmark;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;import java.util.concurrent.atomic.AtomicBoolean;public class Benchmark {    private ExecutorService fileService = Executors.newSingleThreadExecutor();    private ExecutorService hashService = Executors.newSingleThreadExecutor();    private ExecutorService cpuService = Executors.newSingleThreadExecutor();    private final AtomicBoolean isRunning = new AtomicBoolean(false);    public void start(){        Log.d("TEST", "benchmark start");        isRunning.set(true);        cpuService.execute(() -> {            while (isRunning.get()) {                IBenchmark benchmark = new CPUBenchmark();                benchmark.initialize();                benchmark.run();            }        });        hashService.execute(() -> {            while (isRunning.get()) {                IBenchmark benchmark = new HashingBenchmark();                benchmark.initialize();                benchmark.run();            }        });        fileService.execute(() -> {            while (isRunning.get()) {                IBenchmark benchmark = new FilesBenchmark();                benchmark.initialize();                benchmark.run();            }        });    }    public void stop(){        Log.d("TEST", "benchmark stop");        isRunning.set(false);        fileService.shutdown();        hashService.shutdown();        cpuService.shutdown();        fileService = Executors.newSingleThreadExecutor();        hashService = Executors.newSingleThreadExecutor();        cpuService = Executors.newSingleThreadExecutor();    }}